//Función para mostrar la hora y la fecha actual
function mostrarHora() {
  let ahora = new Date();
  let hora = ahora.getHours();
  let minutos = ahora.getMinutes();
  let segundos = ahora.getSeconds();
  let diaSemana = ahora.toLocaleDateString(undefined, {weekday: "long"});
  let fecha = ahora.toLocaleDateString(undefined, {year: "numeric", month: "long", day: "numeric"});
  let horaCompleta = hora + ":" + minutos + ":" + segundos + " - " + diaSemana + ", " + fecha;
  document.getElementById("reloj").textContent = horaCompleta;
}
//Evento para que la funcion se repita cada segundo 
setInterval(mostrarHora, 1000);// hace que se repita la funcion cada 1000 milisegundos
mostrarHora(); //muestra la hora al iniciar la pagina 
//Funcion pregunta que hace que, cada 3 min se te pregunte si quieres seguir en la pagina
function pregunta(){
  if (confirm("LLevas 3 minutos en esta pagina, quieres continuar en la pagina o quieres marcharte,(pulsa confirmar para cambiar de pagina)")) {
    window.location.href = 'https://aaronortiz1601.github.io/paginaweb.github.io-/'; 
    
  } else
   {
    
    alert("En 3 minutos te volvere a preguntar")
  }
}
setInterval(pregunta, 180000);
// Funcion que muestra las medidas de la pagina web en tiempo real
function medidas()
{
  let ancho = window.innerWidth;
  let alto = window.innerHeight;
  document.getElementById("ancho").textContent = "El ancho mide " + ancho;
  document.getElementById("alto").textContent = "El alto mide "+ alto;
}
setInterval(medidas, 1000);
